package javaclass;

public class swaping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1,num2,temp;
		num1=2;
		num2=7;
		System.out.println("Value of first number before swapping nunm1= "+num1 +"  and num2= " +num2);
		///System.out.println("Value of second number before swapping "+num2);
		temp =num1;
		num1=num2;
		num2=temp;
		System.out.println("Value of first number after swapping num1= " +num1 +"  and num2= " +num2);
		///System.out.println("Value of second number after swapping" +num2);
	}

}
